<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;class Pessoa extends Model{    protected $fillable = ['nome', 'foto', 'ranking', 'moderador'];    protected $dates = [];    public static $rules = [        'nome' => 'required'    ];    public static $messages = [        'nome.required'    => 'Informe o nome'    ];    // Relacionamentos    public function usuario()    {        return $this->hasOne('App\Models\Usuario', 'pessoa_id', 'id');    }}